INSERT INTO `wp_posts` VALUES (18,1,'2024-01-22 11:27:01','2024-01-22 11:27:01','<!-- wp:cover {\"overlayColor\":\"white\",\"align\":\"full\",\"className\":\"hestia-clear-top-padding\"} -->\n	<div class=\"wp-block-cover alignfull has-white-background-color has-background-dim hestia-clear-top-padding\"><div class=\"wp-block-cover__inner-container\"><!-- wp:heading {\"textAlign\":\"center\",\"style\":{\"color\":{\"text\":\"#3c4858\"}}} -->\n	<h2 class=\"has-text-align-center has-text-color\" style=\"color:#3c4858\">What we stand for</h2>\n	<!-- /wp:heading -->\n	\n	<!-- wp:spacer {\"height\":40} -->\n	<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n	<!-- /wp:spacer -->\n	\n	<!-- wp:columns {\"className\":\"container\"} -->\n	<div class=\"wp-block-columns container\"><!-- wp:column -->\n	<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":70,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n	<div class=\"wp-block-image\"><figure class=\"aligncenter size-large\"><img src=\"http://drumulvietii.local/wp-content/themes/hestia/assets/img/feat1.png\" alt=\"\" class=\"wp-image-70\"/></figure></div>\n	<!-- /wp:image -->\n	\n	<!-- wp:heading {\"textAlign\":\"center\",\"level\":5,\"style\":{\"color\":{\"text\":\"#3c4858\"},\"typography\":{\"fontSize\":18}}} -->\n	<h5 class=\"has-text-align-center has-text-color\" style=\"color:#3c4858;font-size:18px\">Responsive</h5>\n	<!-- /wp:heading -->\n	\n	<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"color\":{\"text\":\"#999999\"},\"typography\":{\"fontSize\":14}}} -->\n	<p class=\"has-text-align-center has-text-color\" style=\"color:#999999;font-size:14px\">Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>\n	<!-- /wp:paragraph -->\n	\n	<!-- wp:spacer {\"height\":25} -->\n	<div style=\"height:25px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n	<!-- /wp:spacer --></div>\n	<!-- /wp:column -->\n	\n	<!-- wp:column -->\n	<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":71,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n	<div class=\"wp-block-image\"><figure class=\"aligncenter size-large\"><img src=\"http://drumulvietii.local/wp-content/themes/hestia/assets/img/feat2.png\" alt=\"\" class=\"wp-image-71\"/></figure></div>\n	<!-- /wp:image -->\n	\n	<!-- wp:heading {\"textAlign\":\"center\",\"level\":5,\"style\":{\"color\":{\"text\":\"#3c4858\"},\"typography\":{\"fontSize\":18}}} -->\n	<h5 class=\"has-text-align-center has-text-color\" style=\"color:#3c4858;font-size:18px\">Quality</h5>\n	<!-- /wp:heading -->\n	\n	<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"color\":{\"text\":\"#999999\"},\"typography\":{\"fontSize\":14}}} -->\n	<p class=\"has-text-align-center has-text-color\" style=\"color:#999999;font-size:14px\">Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>\n	<!-- /wp:paragraph -->\n	\n	<!-- wp:spacer {\"height\":25} -->\n	<div style=\"height:25px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n	<!-- /wp:spacer --></div>\n	<!-- /wp:column -->\n	\n	<!-- wp:column -->\n	<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":72,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n	<div class=\"wp-block-image\"><figure class=\"aligncenter size-large\"><img src=\"http://drumulvietii.local/wp-content/themes/hestia/assets/img/feat3.png\" alt=\"\" class=\"wp-image-72\"/></figure></div>\n	<!-- /wp:image -->\n	\n	<!-- wp:heading {\"textAlign\":\"center\",\"level\":5,\"style\":{\"color\":{\"text\":\"#3c4858\"},\"typography\":{\"fontSize\":18}}} -->\n	<h5 class=\"has-text-align-center has-text-color\" style=\"color:#3c4858;font-size:18px\">Support</h5>\n	<!-- /wp:heading -->\n	\n	<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"color\":{\"text\":\"#999999\"},\"typography\":{\"fontSize\":14}}} -->\n	<p class=\"has-text-align-center has-text-color\" style=\"color:#999999;font-size:14px\">Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>\n	<!-- /wp:paragraph -->\n	\n	<!-- wp:spacer {\"height\":25} -->\n	<div style=\"height:25px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n	<!-- /wp:spacer --></div>\n	<!-- /wp:column --></div>\n	<!-- /wp:columns -->\n	\n	<!-- wp:spacer {\"height\":40} -->\n	<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n	<!-- /wp:spacer --></div></div>\n	<!-- /wp:cover -->\n	\n	<!-- wp:columns {\"className\":\"container\"} -->\n	<div class=\"wp-block-columns container\"><!-- wp:column {\"className\":\"col-md-5\"} -->\n	<div class=\"wp-block-column col-md-5\"><!-- wp:spacer {\"height\":55} -->\n	<div style=\"height:55px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n	<!-- /wp:spacer -->\n	\n	<!-- wp:heading -->\n	<h2>About Hestia</h2>\n	<!-- /wp:heading -->\n	\n	<!-- wp:paragraph -->\n	<p>Need more details? Please check our full documentation for detailed information on how to use Hestia.</p>\n	<!-- /wp:paragraph --></div>\n	<!-- /wp:column -->\n	\n	<!-- wp:column {\"className\":\"col-md-6 col-md-offset1\"} -->\n	<div class=\"wp-block-column col-md-6 col-md-offset1\"><!-- wp:spacer {\"height\":55} -->\n	<div style=\"height:55px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n	<!-- /wp:spacer -->\n	\n	<!-- wp:image {\"id\":81,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n	<figure class=\"wp-block-image size-large\"><img src=\"http://drumulvietii.local/wp-content/themes/hestia/assets/img/about-content.png\" alt=\"\" class=\"wp-image-81\"/></figure>\n	<!-- /wp:image -->\n	\n	<!-- wp:paragraph -->\n	<p></p>\n	<!-- /wp:paragraph --></div>\n	<!-- /wp:column --></div>\n	<!-- /wp:columns -->','Home','','publish','closed','closed','','home','','','2024-01-22 11:27:01','2024-01-22 11:27:01','',0,'http://drumulvietii.local/?page_id=18',0,'page','',0);
INSERT INTO `wp_posts` VALUES (19,1,'2024-01-22 11:27:01','2024-01-22 11:27:01','<!-- wp:columns {\"align\":\"full\"} -->\n		<div class=\"wp-block-columns alignfull\"><!-- wp:column -->\n		<div class=\"wp-block-column\"><!-- wp:html -->\n		<div style=\"width: 100%\"><iframe width=\"100%\" height=\"400\" frameborder=\"0\" scrolling=\"no\" marginheight=\"0\" marginwidth=\"0\" src=\"https://maps.google.com/maps?width=100%25&amp;height=400&amp;hl=en&amp;q=povernei%2020+(My%20Business%20Name)&amp;t=&amp;z=14&amp;ie=UTF8&amp;iwloc=B&amp;output=embed\"></iframe></div>\n		<!-- /wp:html -->\n		\n		<!-- wp:paragraph -->\n		<p></p>\n		<!-- /wp:paragraph --></div>\n		<!-- /wp:column --></div>\n		<!-- /wp:columns -->\n		\n		<!-- wp:columns {\"className\":\"container\"} -->\n		<div class=\"wp-block-columns container\"><!-- wp:column {\"verticalAlignment\":\"center\"} -->\n		<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:heading {\"className\":\"hestia-title\"} -->\n		<h2 class=\"hestia-title\">Send us a message</h2>\n		<!-- /wp:heading -->\n		\n		<!-- wp:spacer {\"height\":40} -->\n		<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n		<!-- /wp:spacer -->\n		\n		<!-- wp:html -->\n		<div class=\"contact-wrapper\">\n																			<div class=\"content\">\n			\n			  <form>\n			   <div class=\"form-group\">\n			    <input type=\"text\" id=\"fname\" name=\"firstname\" placeholder=\"Your name..\">\n			</div>\n			<div class=\"form-group\">\n			    <input type=\"text\" id=\"lname\" name=\"lastname\" placeholder=\"Your last name..\">\n			</div>\n			<div class=\"form-group\">\n			    <textarea id=\"subject\" name=\"subject\" placeholder=\"Write something..\" style=\"height:200px\"></textarea>\n			</div>\n			    <input type=\"submit\" value=\"Submit\">\n			\n			  </form>\n			</div>\n			</div>\n		<!-- /wp:html --></div>\n		<!-- /wp:column -->\n		\n		<!-- wp:column {\"verticalAlignment\":\"center\"} -->\n		<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:columns -->\n		<div class=\"wp-block-columns\"><!-- wp:column -->\n		<div class=\"wp-block-column\"></div>\n		<!-- /wp:column -->\n		\n		<!-- wp:column {\"width\":\"50%\"} -->\n		<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:heading {\"textAlign\":\"left\",\"level\":4,\"className\":\"hestia-title\",\"style\":{\"typography\":{\"fontSize\":18},\"color\":{\"text\":\"#3c4858\"}}} -->\n		<h4 class=\"has-text-align-left hestia-title has-text-color\" style=\"color:#3c4858;font-size:18px\"><img class=\"wp-image-86\" style=\"width: 25px;\" src=\"http://drumulvietii.local/wp-content/themes/hestia/assets/img/contact1.png\" alt=\"Map icon\">  <strong>Find us at the office</strong></h4>\n		<!-- /wp:heading -->\n		\n		<!-- wp:paragraph {\"align\":\"left\",\"style\":{\"typography\":{\"fontSize\":14},\"color\":{\"text\":\"#999999\"}}} -->\n		<p class=\"has-text-align-left has-text-color\" style=\"color:#999999;font-size:14px\">Strada Povernei, nr 20<br>Bucharest<br>Romania</p>\n		<!-- /wp:paragraph -->\n		\n		<!-- wp:spacer {\"height\":40} -->\n		<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n		<!-- /wp:spacer -->\n		\n		<!-- wp:heading {\"textAlign\":\"left\",\"level\":4,\"className\":\"hestia-title\",\"style\":{\"typography\":{\"fontSize\":18},\"color\":{\"text\":\"#3c4858\"}}} -->\n		<h4 class=\"has-text-align-left hestia-title has-text-color\" style=\"color:#3c4858;font-size:18px\"><img class=\"wp-image-98\" style=\"width: 25px;\" src=\"http://drumulvietii.local/wp-content/themes/hestia/assets/img/contact2.png\" alt=\"\">  <strong><strong>Give us a ring</strong></strong></h4>\n		<!-- /wp:heading -->\n		\n		<!-- wp:paragraph {\"align\":\"left\",\"style\":{\"color\":{\"text\":\"#999999\"},\"typography\":{\"fontSize\":14}}} -->\n		<p class=\"has-text-align-left has-text-color\" style=\"color:#999999;font-size:14px\">John Doe<br>+40 712 345 678<br>Mon - Fri, 8:00-22:00</p>\n		<!-- /wp:paragrap