INSERT INTO `wp_posts` VALUES (29,1,'2024-01-22 11:27:01','2024-01-22 11:27:01','<!-- wp:columns {\"align\":\"full\"} -->\n		<div class=\"wp-block-columns alignfull\"><!-- wp:column -->\n		<div class=\"wp-block-column\"><!-- wp:html -->\n		<div style=\"width: 100%\"><iframe width=\"100%\" height=\"400\" frameborder=\"0\" scrolling=\"no\" marginheight=\"0\" marginwidth=\"0\" src=\"https://maps.google.com/maps?width=100%25&amp;height=400&amp;hl=en&amp;q=povernei%2020+(My%20Business%20Name)&amp;t=&amp;z=14&amp;ie=UTF8&amp;iwloc=B&amp;output=embed\"></iframe></div>\n		<!-- /wp:html -->\n		\n		<!-- wp:paragraph -->\n		<p></p>\n		<!-- /wp:paragraph --></div>\n		<!-- /wp:column --></div>\n		<!-- /wp:columns -->\n		\n		<!-- wp:columns {\"className\":\"container\"} -->\n		<div class=\"wp-block-columns container\"><!-- wp:column {\"verticalAlignment\":\"center\"} -->\n		<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:heading {\"className\":\"hestia-title\"} -->\n		<h2 class=\"hestia-title\">Send us a message</h2>\n		<!-- /wp:heading -->\n		\n		<!-- wp:spacer {\"height\":40} -->\n		<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n		<!-- /wp:spacer -->\n		\n		<!-- wp:html -->\n		<div class=\"contact-wrapper\">\n																			<div class=\"content\">\n			\n			  <form>\n			   <div class=\"form-group\">\n			    <input type=\"text\" id=\"fname\" name=\"firstname\" placeholder=\"Your name..\">\n			</div>\n			<div class=\"form-group\">\n			    <input type=\"text\" id=\"lname\" name=\"lastname\" placeholder=\"Your last name..\">\n			</div>\n			<div class=\"form-group\">\n			    <textarea id=\"subject\" name=\"subject\" placeholder=\"Write something..\" style=\"height:200px\"></textarea>\n			</div>\n			    <input type=\"submit\" value=\"Submit\">\n			\n			  </form>\n			</div>\n			</div>\n		<!-- /wp:html --></div>\n		<!-- /wp:column -->\n		\n		<!-- wp:column {\"verticalAlignment\":\"center\"} -->\n		<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:columns -->\n		<div class=\"wp-block-columns\"><!-- wp:column -->\n		<div class=\"wp-block-column\"></div>\n		<!-- /wp:column -->\n		\n		<!-- wp:column {\"width\":\"50%\"} -->\n		<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:heading {\"textAlign\":\"left\",\"level\":4,\"className\":\"hestia-title\",\"style\":{\"typography\":{\"fontSize\":18},\"color\":{\"text\":\"#3c4858\"}}} -->\n		<h4 class=\"has-text-align-left hestia-title has-text-color\" style=\"color:#3c4858;font-size:18px\"><img class=\"wp-image-86\" style=\"width: 25px;\" src=\"http://drumulvietii.local/wp-content/themes/hestia/assets/img/contact1.png\" alt=\"Map icon\">  <strong>Find us at the office</strong></h4>\n		<!-- /wp:heading -->\n		\n		<!-- wp:paragraph {\"align\":\"left\",\"style\":{\"typography\":{\"fontSize\":14},\"color\":{\"text\":\"#999999\"}}} -->\n		<p class=\"has-text-align-left has-text-color\" style=\"color:#999999;font-size:14px\">Strada Povernei, nr 20<br>Bucharest<br>Romania</p>\n		<!-- /wp:paragraph -->\n		\n		<!-- wp:spacer {\"height\":40} -->\n		<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n		<!-- /wp:spacer -->\n		\n		<!-- wp:heading {\"textAlign\":\"left\",\"level\":4,\"className\":\"hestia-title\",\"style\":{\"typography\":{\"fontSize\":18},\"color\":{\"text\":\"#3c4858\"}}} -->\n		<h4 class=\"has-text-align-left hestia-title has-text-color\" style=\"color:#3c4858;font-size:18px\"><img class=\"wp-image-98\" style=\"width: 25px;\" src=\"http://drumulvietii.local/wp-content/themes/hestia/assets/img/contact2.png\" alt=\"\">  <strong><strong>Give us a ring</strong></strong></h4>\n		<!-- /wp:heading -->\n		\n		<!-- wp:paragraph {\"align\":\"left\",\"style\":{\"color\":{\"text\":\"#999999\"},\"typography\":{\"fontSize\":14}}} -->\n		<p class=\"has-text-align-left has-text-color\" style=\"color:#999999;font-size:14px\">John Doe<br>+40 712 345 678<br>Mon - Fri, 8:00-22:00</p>\n		<!-- /wp:paragraph -->\n		\n		<!-- wp:spacer {\"height\":40} -->\n		<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n		<!-- /wp:spacer -->\n		\n		<!-- wp:heading {\"textAlign\":\"left\",\"level\":4,\"className\":\"hestia-title\",\"style\":{\"color\":{\"text\":\"#3c4858\"},\"typography\":{\"fontSize\":18}}} -->\n		<h4 class=\"has-text-align-left hestia-title has-text-color\" style=\"color:#3c4858;font-size:18px\"><img class=\"wp-image-105\" style=\"width: 25px;\" src=\"http://drumulvietii.local/wp-content/themes/hestia/assets/img/contact3.png\" alt=\"\"><strong>  <strong>Legal information</strong></strong></h4>\n		<!-- /wp:heading -->\n		\n		<!-- wp:paragraph {\"align\":\"left\",\"style\":{\"color\":{\"text\":\"#999999\"},\"typography\":{\"fontSize\":14}}} -->\n		<p class=\"has-text-align-left has-text-color\" style=\"color:#999999;font-size:14px\">John Doe Co.<br>Fiscal Code: 12345678</p>\n		<!-- /wp:paragraph --></div>\n		<!-- /wp:column -->\n		\n		<!-- wp:column -->\n		<div class=\"wp-block-column\"></div>\n		<!-- /wp:column --></div>\n		<!-- /wp:columns --></div>\n		<!-- /wp:column --></div>\n		<!-- /wp:columns -->\n		\n		<!-- wp:paragraph -->\n		<p></p>\n		<!-- /wp:paragraph -->','Contact','','inherit','closed','closed','','19-revision-v1','','','2024-01-22 11:27:01','2024-01-22 11:27:01','',19,'http://drumulvietii.local/?p=29',0,'revision','',0);
